import React from "react";
import Card from "../../../../../02_react_basics_&_working_with_components/financial_tracker/src/components/UI/Card";
import "./AddUser.css";

const AddUser = (props) => {
  const addUserHandler = (event) => {
    event.preventDefault();
  };

  return (
    <Card>
      <form className="add-user-form" onSubmit={addUserHandler}>
        <label htmlFor="username">Username</label>
        <input type="text" className="add-user-name" id="username" />
        <label htmlFor="age">Age (Years)</label>
        <input type="number" className="add-user-age" id="age" />
        <button type="submit" className="add-user-submit">
          Add User
        </button>
      </form>
    </Card>
  );
};

export default AddUser;
